package servlet;

import model.Customer;

import javax.servlet.*;
import javax.servlet.http.*;
import javax.servlet.annotation.*;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

@WebServlet(name = "CustomerServlet", value = "/CustomerServlet")
public class CustomerServlet extends HttpServlet {
    private static List<Customer> customerList = new ArrayList<>();

    @Override
    public void init() throws ServletException {
        customerList.add(new Customer("Mai Văn Hoàn","1983-08-20","Hà Nội","data:image/jpeg;base64,"));
        customerList.add(new Customer("Nguyễn Văn Nam","1983-08-21","Bắc Giang","data:image/jpeg;base64,"));
        customerList.add(new Customer("Nguyễn Thái Hòa","1983-08-22","Nam Định","https://media-cdn-v2.laodong.vn/Storage/NewsPortal/2022/2/21/1016216/Carlos-Alcaraz1.jpg"));
        customerList.add(new Customer("Trần Đăng Khoa","1983-08-17","Hà Tây","https://nld.mediacdn.vn/291774122806476800/2021/8/16/9-tin-quan-vot-16291229128861158512935.jpg"));
        customerList.add(new Customer("Nguyễn Đình Thi","1983-08-219","Hà Nội","https://upload.wikimedia.org/wikipedia/commons/thumb/6/6d/Ferrer_US16_%2821%29_%2829749609722%29.jpg/220px-Ferrer_US16_%2821%29_%2829749609722%29.jpg"));
    }

    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        RequestDispatcher requestDispatcher = request.getRequestDispatcher("list/customer/customer.jsp");
        request.setAttribute("customerList",customerList);
        requestDispatcher.forward(request,response);
    }

    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {

    }
}
